let enemySpawnRate = runtime.globalVars.enemySpawnRate;

let runTrig = setInterval(trigger,enemySpawnRate);

runtime.callFunction("SpawnEnemies");

function trigger(){

	if(runtime.globalVars.enemySpawnTracker < runtime.globalVars.enemySpawnCount){
		runtime.globalVars.enemySpawnTracker += 1;
		console.log(runtime.globalVars.enemySpawnTracker)
		//we want to clear the interval to allow updated data
		clearInterval(runTrig);
		runtime.callFunction("SpawnEnemies");
		enemySpawnRate = runtime.globalVars.enemySpawnRate;

		//reset the trigger with updated timing if any
		runTrig = setInterval(trigger, enemySpawnRate);
	} else {
		clearInterval(runTrig);
		console.log('Finished Spawning');
	}


}
